import React from 'react';
import { useState, useEffect,Link } from "react"
import './Home.css'
//Bitcoin Api
function Home() {
  const [coins, setCoins] = useState([])
  const [limit, setLimit] = useState(20)

  async function coin(credentials) {
    await fetch(`https://localhost:44354/WeatherForecast`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(credentials)
    }).then(response => {
      response.json()
        .then(responseJson => {
          setCoins(responseJson)
        });
      })
    }
    const getAllCoin = async () => {
        // const response = await fetch('https://localhost:44354/WeatherForecast');
        // response.json().then((res) => setCoins(res.data));
        // console.log(coins);
          fetch('https://localhost:44354/WeatherForecast', {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
        },
    })
.then(response => response.text())
.then(text => console.log(text))
    };
  
  useEffect(() => {
    fetch("https://localhost:44354/WeatherForecast")
      .then(res => res.json())
      .then(
        (result) => {
          console.log(result);
          // this.setState({
          //   isLoaded: true,
          //   items: result.items
          // });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          console.log("error");
        }
      )
    // fetch('https://localhost:44354/WeatherForecast')
    // .then(response => response.json())
    // .then(data => this.setCoins());


   
  }, [limit])
 
  

  return (
       <section className="coins">
      <table className='table table-hover'>
        <thead>
          <tr>
            <th>CurrentChange</th>
            <th>CurrentExchangeRate</th>
            <th>Key</th>
            <th>LastUpdate</th>
            <th>Unit</th>
          </tr>
        </thead>

        <tbody>
          {coins.map(({ CurrentChange, CurrentExchangeRate, Key, LastUpdate,Unit }) => (
            <tr key={CurrentChange}>
              <td>{CurrentExchangeRate}</td>
              <td>{Key}</td>
              <td>{LastUpdate}</td>
              <td>{Unit}</td>
            </tr>
          ))}
        </tbody>
      </table>

    </section>
  );
}

export default Home;
