// return;
// }
    
    return(
    <div>

            <li>test {arrayholder.length}</li>

    {
         
    data.candidates. map((item,i) => {

            return(
                <li>
                    
                    <button id={item.id} onClick={() => {send_data(item.id);setVisible(!visible);}} style={{ width:'50%' }}>   
                   full name: {item.first_name + " " +item.last_name}
                   <p>job_title:{item.job_title}</p>
                   avatar:{item.avatar}


                    {visible ? '' : ''}

                    {
            visible&&
            <Candidate send_data={array}/> 
            
            }
                </button > 
                
                    
                </li>
                
            
                )
        })


        }
        
    </div>

    )
}
