import React, { useState,useEffect } from 'react';

export default function Candidates() {
const[candidate,SetCandidates]=useState([]);
const [loading, setLoading] = useState(false);
const data = {
    "a":[
    {
    title: 'React',
    'description': 'State in React'
  }, {
    title: 'Angular',
    'description': 'Introducing Typescript'
  }]
}
// const showlist=async()=>{
//     const output= await fetch('http://localhost:8080/api/candidates');
//     const outputjson = await output.json();
//     SetCandidates(data);
// }
useEffect(()=>{
    fetch('http://localhost:8080/api/candidates').then((res) => res.json())
    .then((data) => {
        SetCandidates(data);
    })
    .catch((err) => {
        console.log(err);
      })
      .finally(() => {
        setLoading(false);
      });
},[]);
if (loading) {
    return <p>Data is loading...</p>;
  }
return(
<div>

  {
  candidate.a.map((i) => {
      
        return(
    
            <li>
                {
                i.title
                }
            </li>
          
            )
      
      
      
       
      
        
      
      
  })
  }

</div>

)
}
