import React, { useState } from 'react';
import './Login.css';

export default function Signup() {
    const [emailError, setEmailError] = useState('')
    const [Error, setError] = useState('')

    const validateEmail = (e) => {
        setEmailError(emailError)
        const regax =/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        if(regax.test(emailError)===false)
        setError("valid email")
        
      
      }
  return(
    <div className="login-wrapper">
      <h1>Please Log In</h1>
      <form>
        <label>
          <p>Username</p>
          <input type="text" />
        </label>
        <label>
          <p>Email</p>
          <input type="text" 
          value={emailError}
          onChange={(val) => validateEmail(val)}
          />
          <span style={{
          fontWeight: 'bold',
          color: 'red',
        }}>{Error}</span>
        </label>
        <label>
          <p>Password</p>
          <input type="password" />
        </label>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>
  )
}
