          <p>Username</p>
          <input type="text"
          placeholder='First Name'
          value={user.name} 
         
          />

        </label>
        <label>
          <p>Email</p>
          <input type="text"
          placeholder='Email'
          value={user.email}        
          {...register("email",
                            {
                                required: true,
                                pattern: /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
                            })}
          />
          {errors.email && <p className="text-error" style={{color: "red"}}>Please check the Email</p>}
        </label>
        <span style={{
          fontWeight: 'bold',
