import React, { useState,useEffect } from 'react';
import data from './data.json'
import Candidate from'./candidate'
import usetoken from '../usetoken';
import Login from '../Login';
import { Link } from 'react-router-dom';

export default function Candid() {
const[users,SetCandidates]=useState([]);
const [array, setIArray]= useState([]);
const [visible, setVisible] = React.useState(false);
const [showbutton, setShow]=  React.useState(false);
const [showindex, setShowindex]=  useState();
const [id, setId]= useState();

const logout = () => {
    window.localStorage.removeItem("token");
    window.location.assign("http://localhost:3000/Login")
}

const send_data=(item)=>{  
  item-=1;
//console.log(item)
    setId(item)
    data.candidates.map((value,i)=>{
        if(i===item){
            const propertyValues = Object.entries(value);
            setIArray(propertyValues)
        }
    })
}



const check_id=(e)=>{  
  
   setShowindex(e.target.id)
  console.log(e.target.id)  
}
useEffect(() => {
    showlist()
   }, [])
const showlist=async()=>{
    const requesOptions = {
         headers: {"authorization": "bearer " + usetoken().getToken()}
     }
       const response=await fetch('http://localhost:8080/api/candidates', requesOptions)
       const responsdata =await response.json()
       SetCandidates(responsdata)
    //    responsdata.candidates.map(p=>{
    //     const propertyValue = Object.entries(p);
    //     SetCandidates(propertyValue)   
    //    })
    users.map(i=>{
        console.log(i)
    })

           
     
}


const token = usetoken().getToken();
if(!token) {    
    window.location.replace("/Login")

return;
}
    
    return(
    <div>
            <Link onClick={()=>logout()} className="logout">Press To Logout</Link>

    {
    data.candidates.map((item,i) => {

            return(
                <li>
                    
                    <button id={item.id} onClick={(e) => {send_data(item.id);setVisible(!visible);check_id(e)}} style={{ width:'50%' }}>   
                   full name: {item.first_name + " " +item.last_name}
                   <p>job_title:{item.job_title}</p>
                   avatar:{item.avatar}


                    {visible ? '' : ''}

                    {
            visible&&
            <Candidate send_data={array}/> 
            
            }
                </button > 
                
                    
                </li>
                
            
                )
        })
        }
        
    </div>

    )
}
