import React, { useState } from 'react';
import './Login.css';
import { useForm } from "react-hook-form";
import { Link } from 'react-router-dom';
import Login from './Login'
import { useHistory } from "react-router-dom";
import { useNavigate } from 'react-router-dom';


async function SignupUser(credentials)
{

  console.log(credentials)
 //  let history = useHistory()
  await fetch('http://localhost:8080/api/auth/signup', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(credentials)
  })
  .then(response=>{
    if(response.status===409){
    alert("the user is already")
    }
    if(response.status===200){
      window.location.assign("")
        }
  })
   
      
    

 }

export default function Signup() {
  const [username, setUserName] = useState('');
  const [usererror, setUerror] = useState('');
  const [emailerror, setEmailerror] = useState('');
  const [password, setPassword] = useState();
  const [email, setEmail] = useState();
  const [passerror, setPerror] = useState('');

 
  const onSubmit = async e => {
    e.preventDefault();
    console.log(username)
    const regex_email=/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
   let isvalid=regex_email.test(email)
    if(!isvalid){
         setEmailerror("invalid email ")
      }
      if(username.length<3){
        setUerror("username need more then 3")
      }

        // if(password.length<8){
        //   setPerror("password need to be lest 8 digits")
        // }

      else{
    const token = await SignupUser({
      username,
      email,
      password
    });
    setEmail("")
    setPassword("")
    setUserName("")
    
  }
    
  }

    

  return(
    <div className="login-wrapper">
      <h1>Please Log In</h1>
      <form onSubmit={onSubmit}>  
        <label>
          <p>Username</p>
          <input 
          type="text"
          placeholder='First Name'
          name={'input'}
          onChange={e => setUserName(e.target.value)} 
          />

          <span style={{
          fontWeight: 'bold',
          color: 'red',
        }}>{usererror}</span>
        </label>
        <label>
          <p>Email</p>
          <input type="text"
          placeholder='Email'
          onChange={e => setEmail(e.target.value)}  
          />
          <span style={{
          fontWeight: 'bold',
          color: 'red',
        }}>{emailerror}</span>
        </label>
        <label>
          <p>Password</p>
          <input type="password" 
         placeholder="Password"
         onChange={e => setPassword(e.target.value)}
        
          />

        </label>
        
        <div>
        <button type="submit">Submit</button>
        </div>
      </form>
        
        <div className="link">
        
         <Link to='/Login'>have you register alredy?</Link>
        
         </div>
    </div>
  )
}
