import React, { useState,useEffect } from 'react';
import data from './data.json'
import Candidate from'./candidate'
import getToken from '../usetoken';
import Login from '../Login';

export default function Candid() {
const[users,SetCandidates]=useState([]);
const [array, setIArray]= useState([]);
const [visible, setVisible] = React.useState(false);

const send_data=(item)=>{  
    data.candidates.map((value,i)=>{
        if(i==item){
            const propertyValues = Object.entries(value);
            setIArray(propertyValues)

        }
    })
    
}
const showlist=async()=>{
    // useToken().token
    //const output= await fetch('http://localhost:8080/api/candidates');
   // const outputjson = await output.json();
    SetCandidates(data);
    //console.log(outputjson.andidates)
}
useEffect(()=>{
    showlist()

},[]);

// if(!token) {
//     return <Login setToken={setToken} />
//   }
    const token = getToken();
    if(!token) {
        return <Login setToken={setToken} />
    }
    return(
    <div>

    {
    data.candidates.map((item,i) => {

            return(
                <li>
                    
                    <button value={item.id} onClick={() => {send_data(item.id);setVisible(!visible);}}>   
                    {item.email}
                    {visible ? 'Hide' : 'Show'}

                    {
            visible&&
            <Candidate send_data={array}/> 
            
            }
                </button > 
                
                    
                </li>
                
            
                )
        })
        }
        
    </div>

    )
}
