import React, { useState } from 'react';
import './Login.css';
import { Link } from 'react-router-dom';

async function SignupUser(credentials) {

  await fetch('http://localhost:8080/api/auth/signup', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(credentials)
  })
    .then(response => {
      if (response.status === 409) {
        alert("the user is already exist")
      }
      if (response.status === 200) {
        window.location.assign("http://localhost:3000/Login")
      }
    })

}

export default function Signup() {
  const [username, setUserName] = useState('');
  const [usererror, setUerror] = useState('');
  const [emailerror, setEmailerror] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState();
  const [passerror, setPerror] = useState('');


  const onSubmit = async e => {
    e.preventDefault();
    const regex_email = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    let isvalid = regex_email.test(email)
    if (!isvalid) {
      setEmailerror("invalid email ")
    }
    if (username.length < 3 || username.length > 20) {
      setUerror("username need be least 3 charcters")
    }
    if (password.length < 8 || password.length > 16) {
      setPerror("the password need to be least 8 digits")
    }

    else {
      const token = await SignupUser({
        username,
        email,
        password
      });
      setEmail("")
      setPassword("")
      setUserName("")
    }

  }



  return (
    <div className="login-wrapper">
      <h1>Please Sign Up</h1>
      <form onSubmit={onSubmit}>
        <label>
          <p>Username</p>
          <input
            type="text"
            placeholder='First Name'
            name={'input'}
            onChange={e => setUserName(e.target.value)}
          />
          <span style={{
            fontWeight: 'bold',
            color: 'red',
          }}>{usererror}</span>
        </label>

        <label>
          <p>Email</p>
          <input type="text"
            placeholder='Email'
            onChange={e => setEmail(e.target.value)}
          />
          <span style={{
            fontWeight: 'bold',
            color: 'red',
          }}>{emailerror}</span>
        </label>

        <label>
          <p>Password</p>
          <input type="password"
            placeholder="Password"
            onChange={e => setPassword(e.target.value)}
          />
          <span style={{
            fontWeight: 'bold',
            color: 'red',
          }}>{passerror}</span>
        </label>

        <div>
          <button type="submit">Submit</button>
        </div>
      </form>

      <div className="link">

        <Link to='/Login'>have you register alredy?</Link>

      </div>
    </div>
  )
}
