import React, { useState } from 'react';
import './Login.css';
import { useForm } from "react-hook-form";
import { Link } from 'react-router-dom';
import Login from './Login'

export default function Signup() {

  const [username, setUserName] = useState();
  const [password, setPassword] = useState();
  const [email, setEmail] = useState();
    const { register, handleSubmit, formState: { errors } } = useForm();

    // const validateEmail = (e) => {
    //     const tempemail= e.target.value
    //     setEmailError(tempemail)
    //     const regax =/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    //      if(email==="")
    //      setError("empty email")
    //     if(regax.test(email)===false)
    //     setError("valid email")
        
      
    //   }
      const onSubmit=()=>{
        const user={username,email,password}

      console.log(user.username)
      }
    
  return(
    <div className="login-wrapper">
      <h1>Please Log In</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        <label>
          <p>Username</p>
          <input type="text"
          placeholder='First Name'
          onChange={e => setUserName(e.target.value)} 
          {...register("firstName", { required: true, maxLength: 10 })}
          />
          {errors.firstName && <p className="text-error" style={{color: "red"}} >Please check the First Name</p>}

        </label>
        <label>
          <p>Email</p>
          <input type="text"
          placeholder='Email'
          onChange={e => setEmail(e.target.value)}       
          {...register("email",
                            {
                                required: true,
                                pattern: /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
                            })}
          />
          {errors.email && <p className="text-error" style={{color: "red"}}>Please check the Email</p>}
        </label>
        <span style={{
          fontWeight: 'bold',
          color: 'red',
        }}>{Error}</span>
        <label>
          <p>Password</p>
          <input type="password" 
         placeholder="Password"
         onChange={e => setPassword(e.target.value)}      
          {...register("password", {
                            required: true,
                            pattern: /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,15}$/
                        })}
                        />
                         {errors.password && <p className="text-error" style={{color: "red"}}>Please check the Password</p>}
        </label>
        <div>    
          <button type="submit">Submit</button>
        </div>
        <div className="link">
        
         <Link to='/Login'>have you register alredy?</Link>
        
         </div>
      </form>
    </div>
  )
}
