import React from 'react';
import { useState, useEffect } from "react"
import './Bitcoin.css'

function Bitcoin() {
  const [coins, setCoins] = useState([])
  const [limit, setLimit] = useState(20)

  useEffect(() => {
    const fetchCoins = async () => {
      const res = await fetch(`https://api.coincap.io/v2/assets`)
      const data = await res.json()
      console.log(data.data)
      setCoins(data.data)
    }

    fetchCoins()
  }, [limit])

  const handleRefresh = () => {
    setLimit(20)
    window.scrollTo(0, 0)
  }

  return (
       <section className="coins">
      <h1 style={{ textAlign: "center", marginBottom: "1rem" }}>
        This App uses the <a href="https://coincap.io">CoinCap API</a>
      </h1>
      <article>
        <p>Showing {coins.length} coins</p>
      </article>
      <table className='table table-hover'>
        <thead>
          <tr>
            <th>Rank</th>
            <th>Name</th>
            <th>Price (USD)</th>
            <th>changePercent24Hr</th>
          </tr>
        </thead>

        <tbody>
          {coins.map(({ id, name, rank, priceUsd,changePercent24Hr }) => (
            <tr key={id}>
              <td>{rank}</td>
              <td>{name}</td>
              <td>${parseFloat(priceUsd).toFixed(2)}</td>
              <td>${parseFloat(changePercent24Hr).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="buttons">
        <button onClick={() => setLimit(limit + 20)}>Next</button>
        <button onClick={handleRefresh}>Refresh</button>
      </div>
    </section>
  );
}

export default Bitcoin;
