import React ,{Component}from 'react';
import usetoken from '../usetoken';
import { Link } from 'react-router-dom';

class Welcome extends Component {
    constructor() {
        super()
        this.state = {brand: [], show: false,};

        const token = usetoken().getToken();
if(!token) {    
    window.location.replace("/Login")

return;
}
    }

async componentDidMount(){
    await this.allcandidates()
}


        async allcandidates(){
    
            const requesOptions = {
                 headers: {"authorization": "bearer " + usetoken().getToken()}
             }
               const response=await fetch('http://localhost:8080/api/candidates', requesOptions)
                
               const responsdata =await response.json()
               this.setState({brand:responsdata.candidates})
    
    console.log(this.state.brand)
        
        }
        

          
        toggle = () =>{
             this.setState((currentState) => 
             ({show: !currentState.show}))

            };

    

    render() {

        if (this.state && this.state.brand && this.state.brand.length) {
            
            return(
                <div>
        <Link onClick={this.logout} className="logout">Press To Logout</Link>

              {  
            this.state.brand.map(item=>{
                return(
                    
                    <li>
                        <button id={item.id}  onClick={this.toggle} style={{ width:'50%' }}>   
                    full name: {item.first_name + " " +item.last_name}
                    <p>job_title:{item.job_title}</p>
                    avatar:{item.avatar}
                    
                    {this.state.show ? 'show' : 'hide'}  
        {this.state.show && <p>Hi there</p>}

                    </button>

                    </li>
                )
            })
        }
        </div>
        );
        } else {
            return(
                <div>No candidates</div>
            )
        }
        
    }
    
  }
  export default Welcome;
