import React, { useState } from 'react';
import './Login.css';
import { useForm } from "react-hook-form";
import { Link } from 'react-router-dom';
import Login from './Login'

export default function Signup() {

  const [username, setUserName] = useState('');
  const [usererror, setUerror] = useState('');
  const [emailerror, setEmailerror] = useState('');
  const [password, setPassword] = useState();
  const [email, setEmail] = useState();
    //const { register, handleSubmit, formState: { errors } } = useForm();

    // const validateEmail = (e) => {
    //     const tempemail= e.target.value
    //     setEmailError(tempemail)
    //     const regax =/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    //      if(email==="")
    //      setError("empty email")
    //     if(regax.test(email)===false)
    //     setError("valid email")
        
      
    //   }
      const onSubmit=async()=>{
const user={username,email,password}
const regex_username=/^[[A-Z]|[a-z]]/
const regex_email=/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
if(username===""||regex_username.test(username)){
  setUerror("invalid username ")
}
if(email===""||regex_email.test(email)){
  setEmailerror("invalid email ")
}
else{
try{
  await fetch('http://localhost:8080/api/auth/signup', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(user)
  })
  alert("success signup!"+
        "now login")
}
catch(error){
alert("signup fault")
      }
    } 
      }
  return(
    <div className="login-wrapper">
      <h1>Please Log In</h1>
        <label>
          <p>Username</p>
          <input type="text"
          placeholder='First Name'
          onChange={e => setUserName(e.target.value)} 
          />
          <span style={{
          fontWeight: 'bold',
          color: 'red',
        }}>{usererror}</span>
        </label>
        <label>
          <p>Email</p>
          <input type="text"
          placeholder='Email'
          onChange={e => setEmail(e.target.value)}       
          />
          <span style={{
          fontWeight: 'bold',
          color: 'red',
        }}>{emailerror}</span>
        </label>
        <label>
          <p>Password</p>
          <input type="password" 
         placeholder="Password"
         onChange={e => setPassword(e.target.value)}      
     
                        />
                                </label>
        
        <button onClick={onSubmit}>  Register
        </button>
        
        <div className="link">
        
         <Link to='/Login'>have you register alredy?</Link>
        
         </div>
    </div>
  )
}
