        }}>{Error}</span>
        <label>
          <p>Password</p>
          <input type="password" 
         placeholder="Password"
         onChange={e => setPassword(e.target.value)}      
     
                        />
                                </label>
        
        <button onClick={onSubmit}>  Register
        </button>
        
        <div className="link">
        
         <Link to='/Login'>have you register alredy?</Link>
        
         </div>
    </div>
  )
}
