//       'Content-Type': 'application/json'
//     },
//     body: JSON.stringify(user)
//   })
//   alert("success signup!"+
//         "now login")
// }
// catch(error){
// alert("signup fault")
//       }
//     } 
//       }
  return(
    <div className="login-wrapper">
      <h1>Please Log In</h1>
      <form onSubmit={onSubmit}>  
        <label>
          <p>Username</p>
          <input type="text"
          placeholder='First Name'
          onChange={e => setUserName(e.target.value)} 
          {...register("firstName", { required: true, maxLength: 10 })}
          />
           {errors.firstName && <p className="text-error">Please check the Last Name</p>}

          <span style={{
          fontWeight: 'bold',
          color: 'red',
        }}>{usererror}</span>
        </label>
        <label>
          <p>Email</p>
          <input type="text"
          placeholder='Email'
          onChange={e => setEmail(e.target.value)}  
          {...register("email",
          {
              required: true,
              pattern: /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
          })}     
          />
          {errors.email && <p className="text-error">Please check the Email</p>}
          <span style={{
          fontWeight: 'bold',
          color: 'red',
        }}>{emailerror}</span>
        </label>
        <label>
          <p>Password</p>
          <input type="password" 
         placeholder="Password"
         onChange={e => setPassword(e.target.value)}
         {...register("password", {
          required: true,
          //pattern: /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,15}$/
      })}
          />
        {errors.password && <p className="text-error">Please check the Password</p>}

        </label>
        
        <div>
        <button type="submit">Submit</button>
        </div>
      </form>
        
        <div className="link">
        
         <Link to='/Login'>have you register alredy?</Link>
        
         </div>
    </div>
  )
}
