import React from 'react';
import { useState, useEffect,Link } from "react"
import './Home.css'
//Bitcoin Api
function Home() {
  const [coins, setCoins] = useState([])
  const [limit, setLimit] = useState(20)

  async function coin(credentials) {
    await fetch(`https://localhost:44354/WeatherForecast`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(credentials)
    }).then(response => {
      response.json()
        .then(responseJson => {
          setCoins(responseJson)
        });
      })
    }
    const getAllCoin = async () => {
      const response = await fetch('https://localhost:44354/WeatherForecast');
      response.json().then((res) => setCoins(res.data));
      console.log(res.data);
    };
  
  useEffect(() => {
    getAllCoin();
    // fetch('https://localhost:44354/WeatherForecast')
    // .then(response => response.json())
    // .then(data => this.setCoins());


   
  }, [limit])
 
    

 const  logout = () => {
    window.localStorage.removeItem("token");
    window.location.assign("../Login")
}
   
  const handleRefresh = () => {
    setLimit(20)
    window.scrollTo(0, 0)
  }

  return (
       <section className="coins">
         <button onClick={logout} className="btn btn-outline-info">Press To Logout</button>
      <h1 style={{ textAlign: "center", marginBottom: "1rem" ,color: "#92badd"}}>
         CoinCap API
      </h1>
      <article>
        <p>Showing {coins.length} coins</p>
      </article>
      <table className='table table-hover'>
        <thead>
          <tr>
            <th>CurrentChange</th>
            <th>CurrentExchangeRate</th>
            <th>Key</th>
            <th>LastUpdate</th>
            <th>Unit</th>
          </tr>
        </thead>

        <tbody>
          {coins.map(({ CurrentChange, CurrentExchangeRate, Key, LastUpdate,Unit }) => (
            <tr key={CurrentChange}>
              <td>{CurrentExchangeRate}</td>
              <td>{Key}</td>
              <td>{LastUpdate}</td>
              <td>{Unit}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="buttons">
        <button className='btn btn-outline-dark' onClick={handleRefresh}>Refresh</button>
      </div>
    </section>
  );
}

export default Home;
